src = Flowable.fromCallable(Callable);
src = Flowable.fromFuture(Future);
src = Flowable.fromFuture(future, Scheduler);
src = Flowable.fromFuture(future, long timeout, TimeUnit);
src = Flowable.fromFuture(future, long timeout, TimeUnit, Scheduler);


订阅方式和Observable一样


//3 Single：只收到一次item，所以就是成功和失败
Single src = Single.create(new SingleOnSubscribe() {
    @Override
    public void subscribe(SingleEmitter e) throws Exception {
        e.onSuccess("item"); //e.onError(Throwable)
    }
});
Single src = Single.just(String item);
src = Single.fromCallable(Call);
src = Single.fromFuture(Future);
src = Single.fromFuture(future, Scheduler);
